$(document).ready(function(){var date=new Date();date.setDate(date.getDate()+30);const ye=new Intl.DateTimeFormat('en',{year:'numeric'}).format(date);const mo=new Intl.DateTimeFormat('en',{month:'numeric'}).format(date);const da=new Intl.DateTimeFormat('en',{day:'2-digit'}).format(date);const dateText=ye+'/'+mo+'/'+da});var filterFns={numberGreaterThan50:function(){var number=$(this).find('.number').text();return parseInt(number,10)>50},ium:function(){var name=$(this).find('.name').text();return name.match(/ium$/)}};$('#filters').on('click','button',function(){var filterValue=$(this).attr('data-filter');filterValue=filterFns[filterValue]||filterValue;$grid.isotope({filter:filterValue})});$('#sorts').on('click','button',function(){var sortByValue=$(this).attr('data-sort-by');$grid.isotope({sortBy:sortByValue})});$('.button-group').each(function(i,buttonGroup){var $buttonGroup=$(buttonGroup);$buttonGroup.on('click','button',function(){$buttonGroup.find('.is-checked').removeClass('is-checked');$(this).addClass('is-checked')})})